package Dipanshu;

public class Question2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
demo();
	}
	public static void demo()
	{
		int i,j;
		for(i=1;i<=5;i++)
		{
			for(j=1;j<=2*i-1;j++)
			{
				System.out.print("*");
			}
			System.out.println();
		}
	}

}
